import React, { useReducer } from "react";
import uuid from "uuid";
import ContactContext from "./contactContext";
import contactReducer from "./contactReducer";
import {
  ADD_CONTACT,
  UPDATE_CONTACT,
  DELETE_CONTACT,
  FILTER_CONTACTS,
  SET_CURRENT,
  CLEAR_CURRENT,
  CLEAR_FILTER,
} from "../types";

const ContactState = props => {
  const initialState = {
    contacts: [
      {
        id: "1",
        name: "Yasir",
        email: "yasir@gmail.com",
        phone: "55555555",
        type: "personal",
      },
      {
        id: "2",
        name: "Tahir",
        email: "tahir@hotmail.com",
        phone: "77777777",
        type: "professional",
      },
      {
        id: "3",
        name: "Zubair",
        email: "zubair@hotmail.com",
        phone: "66666666",
        type: "professional",
      },
    ],
  };
  const [state, dispatch] = useReducer(contactReducer, initialState);
  // ADD_CONTACT
  const createContact = contact => {
    contact.id=uuid.v4
  }
  // UPDATE_CONTACT
  // DELETE_CONTACT
  // FILTER_CONTACTS
  // SET_CURRENT
  // CLEAR_CURRENT
  // CLEAR_FILTER
  return (
    <ContactContext.Provider value={{ contacts: state.contacts }}>
      {props.children}
    </ContactContext.Provider>
  );
};

export default ContactState;
