import React, { useReducer } from "react";
import { v4 as uuid } from "uuid";
import ContactContext from "./contactContext";
import contactReducer from "./contactReducer";
import {
  CREATE_CONTACT,
  SET_CURRENT,
  CLEAR_CURRENT,
  UPDATE_CONTACT,
  DELETE_CONTACT,
  FILTER_CONTACTS,
  CLEAR_FILTER,
} from "../types";

const ContactState = props => {
  const initialState = {
    contacts: [
      {
        id: "1",
        name: "Yasir",
        email: "yasir@gmail.com",
        phone: "55555555",
        type: "personal",
      },
      {
        id: "2",
        name: "Tahir",
        email: "tahir@hotmail.com",
        phone: "77777777",
        type: "professional",
      },
      {
        id: "3",
        name: "Zubair",
        email: "zubair@hotmail.com",
        phone: "66666666",
        type: "professional",
      },
    ],
    // temporary object to store the edits
    current: null,
    // search term value
    filtered: null,
  };

  // dispatching functions
  const [state, dispatch] = useReducer(contactReducer, initialState);

  // Crud - create contact
  const createContact = contact => {
    contact.id = uuid.v4;
    dispatch({ type: CREATE_CONTACT, payload: contact });
  };

  // crUd-1 - defines the changes to the current contact
  const setCurrentContact = contact =>
    dispatch({ type: SET_CURRENT, payload: contact });
  // crUd-2 - clear the edit field after changes are made
  const clearCurrentContact = () => dispatch({ type: CLEAR_CURRENT });
  // UPDATE_CONTACT - crUd3 - actually update the contact
  const updateContact = contact =>
    dispatch({ type: UPDATE_CONTACT, payload: contact });
  // DELETE_CONTACT - cruD
  const deleteContact = id => dispatch({ type: DELETE_CONTACT, payload: id });
  // FILTER_CONTACTS
  const searchContact = text =>
    dispatch({ type: FILTER_CONTACTS, payload: text });
  // CLEAR_FILTER
  const clearContactSearch = () => dispatch({ type: CLEAR_FILTER });

  return (
    <ContactContext.Provider
      value={{
        contacts: state.contacts,
        current: state.current,
        filtered: state.filtered,
        createContact,
        setCurrentContact,
        clearCurrentContact,
        updateContact,
        deleteContact,
        searchContact,
        clearContactSearch,
      }}
    >
      {props.children}
    </ContactContext.Provider>
  );
};

export default ContactState;
