import React, { useReducer } from "react";
import axios from "axios";
import ContactContext from "./contactContext";
import contactReducer from "./contactReducer";
import {
  CREATE_CONTACT,
  SET_CURRENT,
  CLEAR_CURRENT,
  UPDATE_CONTACT,
  DELETE_CONTACT,
  FILTER_CONTACTS,
  CLEAR_FILTER,
} from "../types";

const ContactState = props => {
  const initialState = {
    contacts: [],
    // temporary object to store the edits
    current: null,
    // search term value
    filtered: null,
  };

  // dispatching functions
  const [state, dispatch] = useReducer(contactReducer, initialState);

  // create contact
  const createContact = async contact => {
    const config = { headers: { "Content-Type": "application/json" } };

    try {
      const response = await axios.post("/api/contacts", contact, config);
      // if registration successful
      dispatch({ type: REGISTER_SUCCESS, payload: response.data });
      // load user data
      loadUser();
    } catch (err) {
      // if registration failed
      dispatch({ type: REGISTER_FAIL, payload: err.response.data.message });
    }

    dispatch({ type: CREATE_CONTACT, payload: contact });
  };

  // update contact - 1 - defines the changes to the current contact
  const setCurrentContact = contact =>
    dispatch({ type: SET_CURRENT, payload: contact });
  // update contact - 2 - clear the edit field after changes are made
  const clearCurrentContact = () => dispatch({ type: CLEAR_CURRENT });
  // update contact - 3 - actually update the contact
  const updateContact = contact =>
    dispatch({ type: UPDATE_CONTACT, payload: contact });

  // delete contact - cruD
  const deleteContact = id => dispatch({ type: DELETE_CONTACT, payload: id });

  // filter contacts
  const searchContact = text =>
    dispatch({ type: FILTER_CONTACTS, payload: text });
  // clear filters
  const clearContactSearch = () => dispatch({ type: CLEAR_FILTER });

  return (
    <ContactContext.Provider
      value={{
        contacts: state.contacts,
        current: state.current,
        filtered: state.filtered,
        createContact,
        setCurrentContact,
        clearCurrentContact,
        updateContact,
        deleteContact,
        searchContact,
        clearContactSearch,
      }}
    >
      {props.children}
    </ContactContext.Provider>
  );
};

export default ContactState;
