import React, { useState } from "react";

const ContactForm = () => {
  const [contact, setContact] = useState({
    name: "",
    email: "",
    phone: "",
    type: "personal",
  });
  const { name, email, phone, type } = contact;
  const onChange = change =>
    setContact({ ...contact, [change.target.name]: change.target.value });
  return (
    <form className="contact-form">
      <h2>Contact form</h2>
          <input
              className=""
        type="text"
        name="name"
        value={name}
        onChange={onChange}
        placeholder="Name"
      />
      <input
        type="email"
        name="email"
        value={email}
        onChange={onChange}
        placeholder="Email"
      />
      <input
        type="phone"
        name="name"
        value={phone}
        onChange={onChange}
        placeholder="Phone"
      />
      <div>
        <h3>Contact type</h3>
        <input
          type="radio"
          name="type"
          value="personal"
          checked={type === "personal"}
        />
        &nbsp; Personal &nbsp;
        <input
          type="radio"
          name="type"
          value="professional"
          checked={type === "professional"}
        />
        &nbsp; Professional
      </div>
      <div>
        <input
          className="button button-submit"
          type="submit"
          value="Create contact"
        />
      </div>
    </form>
  );
};

export default ContactForm;
