  });
  const { name, email, phone, type } = contact;
  return (
    <form>
      <h2>Contact form</h2>
      <input
        type="text"
        name="name"
        value={name}
        onChange={onChange}
        placeholder="Name"
      />
      <input
        type="email"
        name="email"
        value={email}
        onChange={onChange}
        placeholder="Email"
      />
      <input
        type="phone"
        name="name"
        value={phone}
        onChange={onChange}
        placeholder="Phone"
      />
