import {
  CREATE_CONTACT,
  UPDATE_CONTACT,
  DELETE_CONTACT,
  FILTER_CONTACTS,

  CLEAR_FILTER,
} from "../types";
export default (state, action) => {
  switch (action.type) {
    //Crud
    case CREATE_CONTACT:
      return { ...state, contacts: [...state.contacts, action.payload] };
    // crUd
    case SET_CURRENT:
      return { ...state, current: action.payload };
    case CLEAR_CURRENT:
      return { ...state, current: null };
    //cruD
    case DELETE_CONTACT:
      return {
        ...state,
        contacts: state.contacts.filter(
          contact => contact.id !== action.payload
        ),
      };
    default:
      return state;
  }
};
