import React, { useContext } from "react";

const ContactCard = ({ contact }) => {
  const { id, name, email, phone, type } = contact;
  return (
    <div className="contact-card">
      <h2>
        {name}{" "}
        <span
          className={
            "badge " +
            (type === "professionl" ? "badge-professional" : "badge-personal")
          }
        >
          {type}
        </span>
      </h2>
    </div>
  );
};

export default ContactCard;
