import React, { Fragment, useContext } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import AuthContext from "../../context/authentication/authContext";
import ContactContext from "../../context/contact/contactContext";

const Navbar = ({ title, icon }) => {
  // import authentication context
  const authContext = useContext(AuthContext);
  const { isAuthenticated, logout, user } = authContext;
  // import contact context
  const contactContext = useContext(ContactContext);
  const { clearCurrentContact } = contactContext;

  // logout function
  const onLogout = () => {logout();clear}

  // auth in and auth out links
  const authIn = (
    <Fragment>
      <Link className="navlink" to="/register">
        Register
      </Link>
      <Link className="navlink" to="/login">
        Login
      </Link>
    </Fragment>
  );
  const authOut = (
    <Fragment>
      <Fragment>
        Welcome <strong>{user && user.name}</strong>
      </Fragment>
      <Link className="navlink" to="#!" onClick={onLogout}>
        Logout
      </Link>
    </Fragment>
  );

  return (
    <div className="navbar">
      <div className="nav-title">
        <h1>
          <Link className="navlink" to="/">
            <i className={icon} />
            {title}
          </Link>
        </h1>
      </div>
      <div className="navlinks">{isAuthenticated ? authOut : authIn}</div>
    </div>
  );
};

Navbar.propTypes = {
  title: PropTypes.string.isRequired,
  icon: PropTypes.string,
};
Navbar.defaultProps = {
  title: " Contact Keeper",
  icon: "fas fa-id-card-alt",
};

export default Navbar;
