import React, { useState, useContext, useEffect } from "react";
import ContactContext from "../../context/contact/contactContext";

// CrUd - Create and Update contact
const ContactForm = () => {
  const contactContext = useContext(ContactContext);
  const { createContact, clearCurrentContact,updateContact, current } = contactContext;
  let contactFormTitle;
  // add contact details of current contact if edit button is clicked
  useEffect(() => {
    current
      ? setContact(current)
      : setContact({
          name: "",
          email: "",
          phone: "",
          type: "personal",
        });
  }, [contactContext, current]);
  // default values of the contact form
  const [contact, setContact] = useState({
    name: "",
    email: "",
    phone: "",
    type: "personal",
  });
  const { name, email, phone, type } = contact;
  // assign new values to contact object
  const onChange = change =>
    setContact({ ...contact, [change.target.name]: change.target.value });
  // submit values to new contact
  const onSubmit = submit => {
    submit.preventDefault();
    // add to contacts
      !current?    createContact(contact):updateContact(contact);
    // revert form to default values
    setContact({
      name: "",
      email: "",
      phone: "",
      type: "personal",
    });
  };
  const clearContactForm = () => clearCurrentContact();

  return (
    <form className="contact-form" onSubmit={onSubmit}>
      <h2>{current ? "Update contact" : "Create contact"}</h2>
      {/* input fields */}
      <input
        className="input-field"
        type="text"
        name="name"
        value={name}
        onChange={onChange}
        placeholder="Name"
        required
      />
      <input
        className="input-field"
        type="email"
        name="email"
        value={email}
        onChange={onChange}
        placeholder="Email"
      />
      <input
        className="input-field"
        type="phone"
        name="phone"
        value={phone}
        onChange={onChange}
        placeholder="Phone"
      />
      {/* personal or professional check box */}
      <div>
        <h3>Contact type</h3>
        <input
          type="radio"
          name="type"
          value="personal"
          checked={type === "personal"}
          onChange={onChange}
        />
        &nbsp; Personal &nbsp;
        <input
          type="radio"
          name="type"
          value="professional"
          checked={type === "professional"}
          onChange={onChange}
        />
        &nbsp; Professional
      </div>
      {/* submit and clear button */}
      <div>
        <input
          className="button button-submit"
          type="submit"
          value={current ? "Update contact" : "Create contact"}
        />
      </div>
      <div>
        {current && (
          <button
            className="button button-form-clear"
            onClick={clearContactForm}
          >
            Clear form
          </button>
        )}
      </div>
    </form>
  );
};

export default ContactForm;
