import React, { useContext, useEffect, useState } from "react";
import AlertContext from "../../context/alert/alertContext";
import AuthContext from "../../context/auth/authContext";
import Alerts from "../alert/Alerts";

const Register = () => {
  // import authentication functions
  const authContext = useContext(AuthContext);
  const { register, error } = authContext;
  // import alert functions
  const alertContext = useContext(AlertContext);
  const { setAlert } = alertContext;
  // default value
  const [user, setUser] = useState({
    name: "",
    email: "",
    password: "",
    passwordConfirmation: "",
  });
  const { name, email, password, passwordConfirmation } = user;

  // if the user already exists
  useEffect(() => {
    if (error === `This user already exists`) {
      setAlert(error, `danger`);
      clearErr
    }
  }, [error]);
  // update temporary value
  const onChange = change =>
    setUser({ ...user, [change.target.name]: change.target.value });
  // register the user
  const onSubmit = submit => {
    submit.preventDefault();
    // check if input is correct
    if (password !== passwordConfirmation) {
      setAlert(`Passwords don't match`, `danger`);
    }
    // register user
    register({ name, email, password });
  };

  return (
    <form className="registration-form" onSubmit={onSubmit}>
      <h2>Register with us</h2>
      <input
        className="input-field registration-field"
        type="text"
        name="name"
        value={name}
        onChange={onChange}
        placeholder="Name"
        required
      />
      <input
        className="input-field registration-field"
        type="email"
        name="email"
        value={email}
        onChange={onChange}
        placeholder="Email"
        required
      />
      {/* password fields */}
      <input
        className="input-field registration-field"
        type="password"
        name="password"
        value={password}
        onChange={onChange}
        placeholder="Enter a password"
        minLength="8"
        required
      />
      <input
        className="input-field registration-field"
        type="password"
        name="passwordConfirmation"
        value={passwordConfirmation}
        onChange={onChange}
        placeholder="Confirm your password"
        minLength="8"
        required
      />
      <input
        className="button button-submit registration-field"
        type="submit"
        value="Register"
      />
      <Alerts />
    </form>
  );
};

export default Register;
