import React, { useContext, Fragment } from "react";
import PropTypes from "prop-types";
import ContactContext from "../../context/contact/contactContext";

// cRUD - Read and Delete contacts, link to Update contact
const ContactCard = ({ contact }) => {
  const contactContext = useContext(ContactContext);
  const { deleteContact, setCurrentContact, clearCurrentContact } =
    contactContext;
  const { id, name, email, phone, type } = contact;
  //delete method
  const onDelete = () => {
    deleteContact(id);
    // clear any updating so deleted contacts cannot be updated
    clearCurrentContact();
  };
  // update method
  const onUpdate = () => setCurrentContact(contact);
  return (
    <div className="contact-card">
      <h3>
        {name}{" "}
        <span
          className={
            "badge " +
            (type === "professional" ? "badge-professional" : "badge-personal")
          }
        >
          {type}
        </span>
      </h3>
      <ul className="contact-details">
        <li>
          <i className="fas fa-envelope-open"></i>&nbsp;&nbsp;{email}
        </li>

        {phone ? (
          <li>
            <i className="fas fa-phone"></i>&nbsp;&nbsp;{phone}
          </li>
        ) : (
          <li>
            <i className="fas fa-phone"></i>&nbsp;&nbsp;No phone number
          </li>
        )}
      </ul>
      <p>
        <button className="button button-edit" onClick={onUpdate}>
          Edit
        </button>
        <button className="button button-delete" onClick={onDelete}>
          Delete
        </button>
      </p>
    </div>
  );
};

ContactCard.propTypes = {
  contact: PropTypes.object.isRequired,
};

export default ContactCard;
